function OnPluginInstall() if (GetVariable("yourClasses") ~= nil) then luastmt = "obj = " .. GetVariable("yourClasses") assert (loadstring (luastmt or "")) () yourClasses = obj end
if (GetVariable("excluded") ~= nil) then luastmt = "obj = " .. GetVariable("excluded") assert (loadstring (luastmt or "")) () excluded = obj end
if (GetVariable("priority") ~= nil) then luastmt = "obj = " .. GetVariable("priority") assert (loadstring (luastmt or "")) () priority = obj end
if (GetVariable("abilityList") ~= nil) then luastmt = "obj = " .. GetVariable("abilityList") assert (loadstring (luastmt or "")) () abilityList = obj else resetAbilityList() end
Send_GMCP_Packet("request char") end
function OnPluginBroadcast(msg, id, name, text) -- Look for GMCP handler. if (id == '3e7dedbe37e44942dd46d264') then if (text == "char.base") then res, gmcparg = CallPlugin("3e7dedbe37e44942dd46d264","gmcpval","char.base") luastmt = "gmcpdata = " .. gmcparg --- Convert the serialized string back into a lua table.
assert (loadstring (luastmt or "")) () tier = gmcpdata.tier primaryClass = string.lower(gmcpdata.class) setClasses(string.lower(gmcpdata.classes)) yourClasses[primaryClass] = 1 subClass = gmcpdata.subclass
elseif (text == "char.status") then res, gmcparg = CallPlugin("3e7dedbe37e44942dd46d264","gmcpval","char.status") luastmt = "gmcpdata = " .. gmcparg --- Convert the serialized string back into a lua table.
assert (loadstring (luastmt or "")) () curLevel = gmcpdata.level + (tier * 10) end end end
function Help() ColourNote("orange", "black", GetPluginInfo (GetPluginID (), 3)) end
function setClasses(classList) --GMCP Class order: Mage[0], Cleric[1], Thief[2], Warrior[3], Ranger[4], Paladin[5], Psionicist[6] if string.find(classList, "0") ~= nil then --Mage yourClasses["mage"] = 1 end if string.find(classList, "1") ~= nil then --Cleric yourClasses["cleric"] = 1 end if string.find(classList, "2") ~= nil then --Thief yourClasses["thief"] = 1 end if string.find(classList, "3") ~= nil then --Warrior yourClasses["warrior"] = 1 end if string.find(classList, "4") ~= nil then --Ranger yourClasses["ranger"] = 1 end if string.find(classList, "5") ~= nil then --Paladin yourClasses["paladin"] = 1 end if string.find(classList, "6") ~= nil then --Psionicist yourClasses["psionicist"] = 1 end end
function addClass(name, line, wildcards) local cl = string.lower(tostring(wildcards[1]))
if cl == "war" then cl = "warrior" elseif cl == "thi" then cl = "thief" elseif cl == "cle" then cl = "cleric" elseif cl == "ran" then cl = "ranger" elseif cl == "psi" then cl = "psionicist" elseif cl == "pal" then cl = "paladin" end
if cl == nil or cl == "" or yourClasses[cl] == nil then ColourNote("orange", "", tostring(cl) .. " is an invalid class.") return end
if yourClasses[cl] == 0 then ColourNote("orange", "", "You have successfully added the " .. cl .. " class.") yourClasses[cl] = 1 else ColourNote("orange", "", "You are already flagged as having " .. cl .. " class.") end SetVariable("yourClasses", serialize.save_simple(yourClasses)) end
function remClass(name, line, wildcards) local cl = string.lower(tostring(wildcards[1]))
if cl == "war" then cl = "warrior" elseif cl == "thi" then cl = "thief" elseif cl == "cle" then cl = "cleric" elseif cl == "ran" then cl = "ranger" elseif cl == "psi" then cl = "psionicist" elseif cl == "pal" then cl = "paladin" end
if cl == nil or cl == "" or yourClasses[cl] == nil then ColourNote("orange", "", tostring(cl) .. " is an invalid class.") return end
if cl == string.lower(primaryClass) then ColourNote("orange", "", "You cannot remove your primary class.") return end
if yourClasses[cl] == 1 then ColourNote("orange", "", "You have successfully removed the " .. cl .. " class.") yourClasses[cl] = 0 else ColourNote("orange", "", "You are already flagged as not having " .. cl .. " class.") end SetVariable("yourClasses", serialize.save_simple(yourClasses)) end
function reset(name, line, wildcards) if wildcards[1] == "classes" then ColourNote("orange", "", "Resetting remorts.") for key, value in pairs(yourClasses) do yourClasses[key] = 0 end yourClasses[primaryClass] = 1 SetVariable("yourClasses", serialize.save_simple(yourClasses)) else ColourNote("orange", "", "Resetting all skills to 0%.") resetAbilityList() end end
function showExcluded(name, line, wildcards) ColourNote("orange", "", "You are currently excluding the following:") for key, value in pairs(excluded) do ColourNote("orange", "", " - " .. key) end end
function showPriority(name, line, wildcards) ColourNote("orange", "", "Your priority list:") for key, value in pairs(priority) do ColourNote("orange", "", " - " .. key) end end
function modExcluded(name, line, wildcards) local tempName = string.lower(tostring(wildcards[1]))
if tempName == nil or tempName == "" then ColourNote("orange", "", "Invalid skill or spell.") return end
if excluded[tempName] == nil then excluded[tempName] = 1 ColourNote("orange", "", tempName .. " has been added to the list.") else excluded[tempName] = nil ColourNote("orange", "", tempName .. " has been removed from the list.") end SetVariable("excluded", serialize.save_simple(excluded)) end
function modPriority(name, line, wildcards) local tempName = string.lower(tostring(wildcards[1]))
if tempName == nil or tempName == "" then ColourNote("orange", "", "Invalid skill or spell.") return end
if priority[tempName] == nil then priority[tempName] = 1 ColourNote("orange", "", tempName .. " has been added to the list.") else priority[tempName] = nil ColourNote("orange", "", tempName .. " has been removed from the list.") end SetVariable("priority", serialize.save_simple(priority)) end
function pracSkills(name, line, wildcards) --Check priority list first local tempLimit = 0
--Check priority list first for key, value in pairs(priority) do if abilityList[key] ~= nil then if tonumber(abilityList[key].prac) < 85 then if abilityList[key].sub == 1 then local startInd, endInd = string.find(string.lower(abilityList[key].sublist), string.lower(subClass)) if startInd ~= nil then --We found the subclass if abilityList[key][string.lower(primaryClass)] <= curLevel then Send("prac " .. key .. " full") tempLimit = tempLimit + 1 end end else local canLearn = 0 if abilityList[key].warrior > 0 and yourClasses.warrior == 1 and curLevel >= abilityList[key].warrior then --Has class, skill requires class, and level meets/exceeds requirement. canLearn = 1 elseif abilityList[key].cleric > 0 and yourClasses.cleric == 1 and curLevel >= abilityList[key].cleric then --Has class, skill requires class, and level meets/exceeds requirement. canLearn = 1 elseif abilityList[key].mage > 0 and yourClasses.mage == 1 and curLevel >= abilityList[key].mage then --Has class, skill requires class, and level meets/exceeds requirement. canLearn = 1 elseif abilityList[key].thief > 0 and yourClasses.thief == 1 and curLevel >= abilityList[key].thief then --Has class, skill requires class, and level meets/exceeds requirement. canLearn = 1 elseif abilityList[key].ranger > 0 and yourClasses.ranger == 1 and curLevel >= abilityList[key].ranger then --Has class, skill requires class, and level meets/exceeds requirement. canLearn = 1 elseif abilityList[key].paladin > 0 and yourClasses.paladin == 1 and curLevel >= abilityList[key].paladin then --Has class, skill requires class, and level meets/exceeds requirement. canLearn = 1 elseif abilityList[key].psionicist > 0 and yourClasses.psionicist == 1 and curLevel >= abilityList[key].psionicist then --Has class, skill requires class, and level meets/exceeds requirement. canLearn = 1 end if canLearn == 1 then Send("prac " .. key .. " full") tempLimit = tempLimit + 1 end end end end
if tempLimit >= pracLimit then return end end
--Now check ability list for key, value in pairs(abilityList) do if excluded[key] == nil then --Not on the excluded list if tonumber(value.prac) < 85 then if value.sub == 1 then local startInd, endInd = string.find(string.lower(value.sublist), string.lower(subClass)) if startInd ~= nil then --We found the subclass if value[string.lower(primaryClass)] <= curLevel then Send("prac " .. key .. " full") tempLimit = tempLimit + 1 end end else local canLearn = 0 if value.warrior > 0 and yourClasses.warrior == 1 and curLevel >= value.warrior then --Has class, skill requires class, and level meets/exceeds requirement. canLearn = 1 elseif value.cleric > 0 and yourClasses.cleric == 1 and curLevel >= value.cleric then --Has class, skill requires class, and level meets/exceeds requirement. canLearn = 1 elseif value.mage > 0 and yourClasses.mage == 1 and curLevel >= value.mage then --Has class, skill requires class, and level meets/exceeds requirement. canLearn = 1 elseif value.thief > 0 and yourClasses.thief == 1 and curLevel >= value.thief then --Has class, skill requires class, and level meets/exceeds requirement. canLearn = 1 elseif value.ranger > 0 and yourClasses.ranger == 1 and curLevel >= value.ranger then --Has class, skill requires class, and level meets/exceeds requirement. canLearn = 1 elseif value.paladin > 0 and yourClasses.paladin == 1 and curLevel >= value.paladin then --Has class, skill requires class, and level meets/exceeds requirement. canLearn = 1 elseif value.psionicist > 0 and yourClasses.psionicist == 1 and curLevel >= value.psionicist then --Has class, skill requires class, and level meets/exceeds requirement. canLearn = 1 end if canLearn == 1 then Send("prac " .. key .. " full") tempLimit = tempLimit + 1 end end end end
if tempLimit >= pracLimit then return end end
if tempLimit == 0 then --Found nothing to practice ColourNote("orange", "", "There was nothing to practice.") end end
function setLimit(name, line, wildcards) local tempVal = tonumber(wildcards[1]) if tempVal == nil then ColourNote("orange", "", "Invalid input. Amount must be between 1 and 50.") return end
if tempVal > 0 and tempVal <= 50 then pracLimit = tempVal SetVariable("pracLimit", pracLimit) ColourNote("orange", "", "Prac limit set to: ", "cyan", "", tostring(pracLimit)) else ColourNote("orange", "", "Invalid input. Amount must be between 1 and 50.") end end
function setSkill(name, line, wildcards) if abilityList[wildcards.skill] ~= nil then abilityList[wildcards.skill].prac = wildcards.amt end SetVariable("abilityList", serialize.save_simple(abilityList)) end
function setSkillMax(name, line, wildcards) if abilityList[wildcards.skill] ~= nil then abilityList[wildcards.skill].prac = 85 end SetVariable("abilityList", serialize.save_simple(abilityList)) end
function showClasses(name, line, wildcards) ColourNote("orange", "", "Your class flags are as follows:") if yourClasses.warrior == 1 then ColourNote("orange", "", "Warrior : ", "lime", "", "Yes") else ColourNote("orange", "", "Warrior : ", "red", "", "No") end
if yourClasses.thief == 1 then ColourNote("orange", "", "Thief : ", "lime", "", "Yes") else ColourNote("orange", "", "Thief : ", "red", "", "No") end
if yourClasses.cleric == 1 then ColourNote("orange", "", "Cleric : ", "lime", "", "Yes") else ColourNote("orange", "", "Cleric : ", "red", "", "No") end
if yourClasses.mage == 1 then ColourNote("orange", "", "Mage : ", "lime", "", "Yes") else ColourNote("orange", "", "Mage : ", "red", "", "No") end
if yourClasses.paladin == 1 then ColourNote("orange", "", "Paladin : ", "lime", "", "Yes") else ColourNote("orange", "", "Paladin : ", "red", "", "No") end
if yourClasses.ranger == 1 then ColourNote("orange", "", "Ranger : ", "lime", "", "Yes") else ColourNote("orange", "", "Ranger : ", "red", "", "No") end
if yourClasses.psionicist == 1 then ColourNote("orange", "", "Psionicist: ", "lime", "", "Yes") else ColourNote("orange", "", "Psionicist: ", "red", "", "No") end
end
